import PhetFont from '../../../../scenery-phet/js/PhetFont.js';
import { HBox, Image, Text } from '../../../../scenery/js/imports.js';
import AquaRadioButtonGroup from '../../../../sun/js/AquaRadioButtonGroup.js';
